import React, { Component } from 'react';

class Contact extends Component {
  constructor(props) {
      super(props);
      this.state = {name: '',
                    email: '',
                    comment: '',
                    modalState: false};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.toggleModal = this.toggleModal.bind(this);
  }

  toggleModal(event) {
    this.setState({
        modalState: !this.state.modalState
    });
    event.preventDefault();
  }

  handleChange(event) {
    this.setState({name: event.target.value, email:event.target.value, comment:event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.name + this.state.email + this.state.comment);
    event.preventDefault();
  }

  render() {
    let modalState = this.state.modalState ? "ModalOpen" : "ModalClosed";
    return (
        <div class = "content">
        {/* THE MODAL */}
        <div id={modalState} class="modal">
        {/* MODAL CONTENT */}
        <div class="modalContent">
            <div class="modalHead">
            <span class="close" onClick={this.toggleModal}>
            &times;
            </span>
            <h2>Contact Us</h2>
            </div>
            <div class="modalBody">
            <p>Thank you for submitting your comment! Our team will be in touch with you soon.</p>
            </div>
            <div class="modalFoot">
            <h3>Hand-In-Hand (2019)</h3>
            </div>
        </div>
        </div>
        {/* END OF MODAL */}
        <form class = "form-signin" onSubmit={this.toggleModal}>
            <h2>Connect With Us!</h2>
            <p className="paragraphBody"> Please feel free to contact us on any of our social media platforms. We are always available to address your concerns or answer any questions you may have. 
                You may also contact us directly through our phone number or e-mail. 
            </p>
            <div className="contactSocials">
            <div className="facebook">
                <a href="facebook.com"></a>
            </div>
            <div className="instagram">
                <a href="instagram.com"></a>
            </div>
            <div className="yelp">
                <a href="yelp.com"></a>
                </div>
            </div>
            <img class="person_icon" src={require("../../Assets/resources/people.png")}/>
            <h1>Contact Us</h1>
            <div class="form-container">
                <label for="name"><b>Name</b></label>
                    <input type="text" name="name" size="20" placeholder="Name" value={this.state.name} onChange={this.handleChange}/>
                <label for="email"><b>Email</b></label>
                    <input type="text" name="email" size="20" placeholder="Email Address" value={this.state.email} onChange={this.handleChange} />
                <label for="comment"><b>Comment</b></label>
                    <textarea type="text" name="comment" placeholder="Comment Here..." value={this.state.comment} onChange={this.handleChange}/>
                <input class="submitStyles" id="submitButton" type="submit" value="Submit Form" name = "submit" />
                <input class="submitStyles" type="reset" value ="Reset"/>
            </div>
        </form>
        </div>
    );
  }
}

export default Contact;
